data-handler.script:
function (wgt, option) {
    var createqrcode = function(value) {
        wgt.$setValue('');
        if (wgt.qrcode!=null) {
            wgt.qrcode.clear();
            wgt.qrcode.makeCode(value); 
        } else {
            wgt.qrcode = new QRCode(wgt.$n(),value);
        }
    }
    wgt.setOverride("setValue", createqrcode);
    createqrcode(wgt.getValue());
}
